import { __decorate } from "tslib";
import { Component } from "@angular/core";
import { Servicio } from "../clases/servicio";
import { Usuario } from "../clases/usuario";
import { ServicioService } from "../services/servicio.service";
import { UsuarioService } from "../services/usuario.service";
//import { countries } from "./countries/country-data-store";
let perfilComponent = class perfilComponent {
    //public countries: any = countries;
    constructor(_activRoute, _usuarioService, _servicioService) {
        this._activRoute = _activRoute;
        this._usuarioService = _usuarioService;
        this._servicioService = _servicioService;
        this.urlVal = "";
        this.id = 0;
        this.step = 0;
        this.usuario = new Usuario();
        this.servicios = [];
        this.cantidadServicios = 0;
    }
    ngOnInit() {
        this._activRoute.paramMap.subscribe((params) => {
            this.urlVal = params.get("id") + "";
            this.id = Number(this.urlVal);
        });
        console.log(this.id);
        this._usuarioService.getUsuarioById(this.id)
            .subscribe(async (resultado) => {
            this.usuario = new Usuario(resultado.id, resultado.nombre, resultado.primer_apellido, resultado.segundo_apellido, resultado.email, resultado.password, resultado.nacimiento, resultado.telefono, resultado.pais, resultado.ciudad, resultado.direccion, resultado.foto, resultado.tipo_usuario, resultado.mascotas);
        });
        this._servicioService.getServiciosBySuscriptorId(this.id)
            .subscribe(async (resultado) => {
            for (let i of resultado) {
                let servicio = new Servicio(i['id'], i['nombre'], i['descripcion'], i['precio'], i['puntuacion'], i['activo'], i['imagenes'], i['suscriptor']);
                this.cantidadServicios = ++this.cantidadServicios;
                this.servicios.push(servicio);
            }
        });
        console.log(this.servicios);
    }
    edit() {
        if (this.step == 0) {
            this.step = 1;
        }
        else if (this.step == 1) {
            this.step = 0;
        }
    }
};
perfilComponent = __decorate([
    Component({
        selector: 'perfil-comp',
        templateUrl: 'perfil.component.html',
        styleUrls: ['perfil.component.css'],
        providers: [UsuarioService, ServicioService]
    })
], perfilComponent);
export { perfilComponent };
